import React, { useState } from 'react';

function PrototypeDemo() {
  const [form, setForm] = useState({ name: '', email: '' });
  const [users, setUsers] = useState([]);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  // Manejar cambios en el formulario
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Enviar formulario
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.name || !form.email) {
      alert('Por favor completa nombre y email');
      return;
    }

    setLoading(true);

    try {
      const query = new URLSearchParams(form).toString();
      const res = await fetch(`http://localhost:3001/api/users?${query}`);
      const data = await res.json();

      console.log('Respuesta backend:', data);  // Debuguear respuesta

      setUsers(data.users || []);
      setMessage(data.message);
    } catch (error) {
      alert('Error al contactar al backend');
    }

    setLoading(false);
  };

  return (
    <div style={{ maxWidth: 400, margin: 'auto' }}>
      <h2>Prototype Pattern Demo</h2>

      <form onSubmit={handleSubmit} style={{ marginBottom: 20 }}>
        <div>
          <label>Nombre:</label><br />
          <input
            type="text"
            name="name"
            value={form.name}
            onChange={handleChange}
            disabled={loading}
            required
          />
        </div>
        <div style={{ marginTop: 10 }}>
          <label>Email:</label><br />
          <input
            type="email"
            name="email"
            value={form.email}
            onChange={handleChange}
            disabled={loading}
            required
          />
        </div>
        <button type="submit" disabled={loading} style={{ marginTop: 15 }}>
          {loading ? 'Clonando...' : 'Clonar Usuario'}
        </button>
      </form>

      {message && <p><em>{message}</em></p>}

      <h3>Usuarios Clonados (JSON):</h3>
      <pre>{JSON.stringify(users, null, 2)}</pre>

      <h3>Usuarios Clonados (Lista):</h3>
      {users.length === 0 ? (
        <p>No hay usuarios clonados a√∫n.</p>
      ) : (
        <ul>
          {users.map((user, idx) => (
            <li key={idx}>
              <strong>Nombre:</strong> {user.name} <br />
              <strong>Email:</strong> {user.email}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default PrototypeDemo;
