import React, { useState } from 'react';

function PrototypeDemo() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    setLoading(true);
    setUserData(null);

    try {
      const response = await fetch(
        `http://localhost:3001/api/users?name=${encodeURIComponent(name)}&email=${encodeURIComponent(email)}`
      );
      const data = await response.json();
      setUserData(data);
    } catch (error) {
      console.error('Error fetching user:', error);
      setUserData(null);
    }

    setLoading(false);
  };

  return (
    <div>
      <h2>Prototype Pattern Demo</h2>

      <form onSubmit={handleSubmit} style={{ marginBottom: '1rem' }}>
        <div>
          <label>
            Nombre:{' '}
            <input
              type="text"
              value={name}
              onChange={e => setName(e.target.value)}
              placeholder="Ingresa un nombre"
              required
            />
          </label>
        </div>

        <div>
          <label>
            Email:{' '}
            <input
              type="email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              placeholder="Ingresa un email"
              required
            />
          </label>
        </div>

        <button type="submit" disabled={loading}>
          {loading ? 'Cargando...' : 'Clonar Usuario'}
        </button>
      </form>

      {userData && userData.users && (
  <div>
    <p><strong>Mensaje:</strong> {userData.message}</p>
    <h3>Usuarios clonados:</h3>
    <ul>
      {userData.users.map((user, index) => (
        <li key={index}>
          <strong>Nombre:</strong> {user.name} â€” <strong>Email:</strong> {user.email}
        </li>
      ))}
    </ul>
  </div>
)}

    </div>
  );
}

export default PrototypeDemo;
